package week1.day1;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//byte |1 byte|8 bit|-128 to 127
		byte date = 5;
		
		//short |2 bytes|16 bit| -32768 to 3267
		short num1 = 659;
		
		//int |4 bytes|32 bit| -2,147,483,648 to 2147484647 (10 digits)
		int myNum = 8488;
		
		//long |8 bytes| 64 bits|
		long mobNumber = 9784787878L;
		
		//float|4 bytes|6 to 7 decimal values
		float rupees = 0.0115173783738F;
		
		//double|8 bytes|15 decimals
		double dollars = 0.01151783883838;
		
		//boolean|1 bit| T/F
		boolean result = true;
		
		//char|2 bytes| single char
		char initial = 'E';
		
		System.out.println(date);
		System.out.println(num1);
		System.out.println(date);
		System.out.println(num1);
		System.out.println(myNum);
		System.out.println(mobNumber);
		System.out.println(rupees);
		System.out.println(dollars);
		System.out.println(result);
		System.out.println(initial);
		
		
		
		
		
		
		
		
		
		

	}

}
