package week1.day2;

public class JumpStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//break statement
		for (int i = 1; i <= 5; i++) 
		{
            if (i == 3) 
            {
                break; // Stops the loop when i is 3
            }
           // System.out.println(i);
        }
  //continue statement
		
		for (int i = 0 ; i <=5 ; i++)
		{
			if (i ==3)
			{
				continue;
			}
System.out.println(i);
}}}
