package week1.day2;

public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 10;
		if(number%2==0)
		{
		System.out.println(number + " " + "is an even number");
		}
		else 
		{
			System.out.println("Number is odd");
		
	}

	}}
